INSERT INTO risks VALUES(5,'RSK-005','Dell laptop','Corporate laptops','Security threats to Corporate laptops','Security threats to Database Server affecting Dell laptop',4.0,'HIGH',4.0,0.0,0.0,0.0,replace('[\n  {\n    "control_id": "A.8.28",\n    "control_name": "Endpoint Protection (Antivirus/Anti-malware/EDR)",\n    "category": "Technological Controls",\n    "current_rating": 4,\n    "source": "Questionnaire",\n    "evidence": "User confirmed: ''yes_user'' for endpoint protection installed, active, and regularly updated.",\n    "effectiveness": "HIGH"\n  },\n  {\n    "control_id": "A.8.20",\n    "control_name": "VPN for Remote Access",\n    "category": "Technological Controls",\n    "current_rating": 4,\n    "source": "Questionnaire",\n    "evidence": "User confirmed: ''yes'' for VPN required and enforced for remote access.",\n    "effectiveness": "HIGH"\n  },\n  {\n    "control_id": "A.8.2",\n    "control_name": "Principle of Least Privilege (User without Local Admin Rights)",\n    "category": "Technological Controls",\n    "current_rating": 5,\n    "source": "Questionnaire",\n    "evidence": "User confirmed: ''no'' to having local administrator rights.",\n    "effectiveness": "VERY HIGH"\n  }\n]','\n',char(10)),1.43999999999999994,2.56000000000000005,'Unknown',replace('[\n  {\n    "gap_description": "Full Disk Encryption missing",\n    "evidence": "User indicated: ''no'' to full disk encryption being enabled and enforced.",\n    "impact": "Increases the risk of sensitive data compromise if the laptop is lost or stolen.",\n    "severity": "HIGH"\n  },\n  {\n    "gap_description": "Multi-Factor Authentication (MFA) missing",\n    "evidence": "User indicated: ''no'' to MFA being required for logging into the laptop or accessing corporate resources.",\n    "impact": "Increases the probability of unauthorized access through compromised credentials.",\n    "severity": "HIGH"\n  },\n  {\n    "gap_description": "Regular OS and Application Patching missing",\n    "evidence": "User indicated: ''no'' to operating system and application security patches being applied regularly and automatically.",\n    "impact": "Leaves the laptop vulnerable to known exploits and security vulnerabilities.",\n    "severity": "HIGH"\n  },\n  {\n    "gap_description": "Critical User Data Backup missing",\n    "evidence": "User indicated: ''no'' to critical user data being regularly backed up to a corporate-managed storage solution.",\n    "impact": "Risk of permanent data loss in case of system failure, malware infection, or device theft.",\n    "severity": "HIGH"\n  },\n  {\n    "gap_description": "Data Loss Prevention (DLP) or equivalent controls missing",\n    "evidence": "User indicated: ''no'' to DLP software or equivalent controls being implemented to prevent unauthorized data exfiltration.",\n    "impact": "Increases the risk of sensitive data being exfiltrated from the laptop.",\n    "severity": "HIGH"\n  },\n  {\n    "gap_description": "MDM/Endpoint Management with Remote Wipe/Lock missing",\n    "evidence": "User indicated: ''no'' to the laptop being enrolled in an MDM or Endpoint Management solution with remote wipe/lock capabilities.",\n    "impact": "Inability to remotely secure or wipe data on a lost or stolen laptop, leading to potential data breach.",\n    "severity": "HIGH"\n  }\n]','\n',char(10)),replace('[\n  {\n    "control_name": "Implement Full Disk Encryption",\n    "label": "Implement Full Disk Encryption",\n    "description": "Enforce full disk encryption on all Dell laptops to protect data at rest.",\n    "priority": "High",\n    "control_type": "Technical",\n    "cost": "Estimated $50 per device",\n    "timeline": "3 months",\n    "risk_reduction": "Significant",\n    "complexity": "Medium"\n  },\n  {\n    "control_name": "Mandate Multi-Factor Authentication (MFA)",\n    "label": "Mandate Multi-Factor Authentication (MFA)",\n    "description": "Require MFA for all laptop logins and access to corporate resources.",\n    "priori