 {\n          "control_id": "A.8.1",\n          "control_name": "Implement Remote wipe/lock for user endpoint devices",\n          "control_type": "Corrective",\n          "priority": "HIGH",\n          "rationale": "Provides a critical mechanism to protect data on lost or stolen devices, preventing sensitive data exposure.",\n          "expected_effectiveness": 4\n        },\n        {\n          "control_id": "A.5.1",\n          "control_name": "Strengthen enforcement of Physical security policies for information security",\n          "control_type": "Preventive",\n          "priority": "MEDIUM",\n          "rationale": "Improved enforcement of physical security policies can reduce the likelihood of physical theft.",\n          "expected_effectiveness": 3\n        }\n      ]\n    },\n    {\n      "threat": "Data Loss or Corruption from System Failure or Undetected Malware",\n      "vulnerabilities": [\n        "Absence of Endpoint Detection and Response (EDR) or Antivirus/Anti-malware solution"\n      ],\n      "controls_identified": [\n        {\n          "control_id": "A.8.13",\n          "control_name": "Information backup (Automated Backup Solution)",\n          "category": "Corrective",\n          "current_rating": 4,\n          "source": "Questionnaire",\n          "evidence": "User confirmed: ''Yes, automated to cloud/network storage''",\n          "effectiveness": "STRONG"\n        }\n      ],\n      "control_rating_calculation": {\n        "weighted_preventive": "0.0 \u00d7 1.0 = 0.0",\n        "weighted_detective": "0.0 \u00d7 0.75 = 0.0",\n        "weighted_corrective": "4.0 \u00d7 0.5 = 2.0",\n        "average_weighted": 0.6666666666666666,\n        "control_rating": 0,\n        "control_rating_text": "WEAK"\n      },\n      "control_category_averages": {\n        "preventive_avg": 0.0,\n        "detective_avg": 0.0,\n        "corrective_avg": 4.0\n      },\n      "risk_evaluation_rating": 5,\n      "residual_risk": {\n        "risk_evaluation_rating"