INSERT INTO risks VALUES(17,'RSK-017','John Doe''s Laptop','Physical Assets','Physical Theft and Sensitive Data Exposure','Risk of physical theft of John Doe''s Laptop and subsequent data exposure due to non-enforcement of physical security policies and unknown remote wipe/lock capabilities, potentially causing unauthorized disclosure of Restricted data (PHI, PII, PCI_DSS, Financial_Proprietary), regulatory non-compliance, and reputational damage.',4.0,'4',4.0,'Low','Low','Low',replace('[\n  {\n    "control_id": "A.8.23",\n    "control_name": "Use of cryptography (Full Disk Encryption)",\n    "category": "Preventive",\n    "current_rating": 4,\n    "source": "Questionnaire",\n    "evidence": "User confirmed: ''Yes''",\n    "effectiveness": "STRONG"\n  },\n  {\n    "control_id": "A.7.7 / A.7.9",\n    "control_name": "Physical Security Policies/Guidelines (e.g., Clear desk and clear screen policy, Security of assets off-premises)",\n    "category": "Preventive",\n    "current_rating": 2,\n    "source": "Questionnaire",\n    "evidence": "User confirmed: ''Yes, but not strictly enforced''",\n    "effectiveness": "LIMITED"\n  }\n]','\n',char(10)),1.0,3.0,'Unknown',replace('[\n  {\n    "gap_description": "Remote Wipe or Lock capability unknown/missing",\n    "evidence": "User indicated: ''Unknown'' for Question 18",\n    "impact": "Inability to remotely secure or wipe a lost/stolen laptop increases the risk of sensitive data exposure.",\n    "severity": "HIGH"\n  }\n]','\n',char(10)),replace('[\n  {\n    "control_id": "A.8.1",\n    "control_name": "Implement Remote wipe/lock for user endpoint devices",\n    "control_type": "Corrective",\n    "priority": "HIGH",\n    "rationale": "Provides a critical mechanism to protect data on lost or stolen devices, preventing sensitive data exposure.",\n    "expected_effectiveness": 4\n  },\n  {\n    "control_id": "A.5.1",\n    "control_name": "Strengthen enforcement of Physical security policies for information security",\n    "control_type": "Preventive",\n    "priority": "MEDIUM",\n    "rationale": "Improved enforcement of physical security policies can reduce the likelihood of physical theft.",\n    "expected_effectiveness": 3\n  }\n]','\n',char(10)),NULL,NULL,'TERMINATE',' IT Department','High',NULL,'Open','2026-01-19','2026-01-19',NULL,NULL,NULL,replace('{\n  "discovery_summary": {\n    "cia_methodology": "CIA (Confidentiality, Integrity, Availability)",\n    "cia_rating_scale": "4-level: Insignificant, Moderate, Serious, Extreme",\n    "cia_definitions_source": "Asset Management Guidelines_For RM Agentic AI Use Case v1.0.docx (Table 3)",\n    "business_value_calculation_method": "Asset Value Chart (Matrix)",\n    "business_value_levels": [\n      "Very Low",\n      "Low",\n      "Medium",\n      "High",\n      "Very High"\n    ],\n    "business_value_source": "Risk Assessment Template_For Agentic AI RM Use Case.xlsx (Sheet: Asset Inventorization Guideline)",\n    "criticality_classification_method": "Business Value to Criticality Mapping",\n    "criticality_levels": [\n      "Insignificant",\n      "Low Critical",\n      "Medium Critical",\n      "High Critical",\n      "Very high Critical"\n    ],\n    "criticality_source": "Asset Management Guidelines_For RM Agentic AI Use Case v1.0.docx (Table 4), Risk Assessment Template_For Agentic AI RM Use Case.xlsx (Sheet: Asset Inventorization Guideline)",\n    "searches_performed": [\n      "What impact assessment methodology is used? CIA? DREAD? Custom?",\n      "What rating scale is used for Confidentiality, Integrity, and Availability (CIA) impact ratings? What are the levels and definitions for each?",\n      "How is Asset Business Value calculated or determined from CIA impact ratings? Is there a matrix, chart, or formula? What are the Asset Business Value levels?",\n      "What are the numeric values for Insignificant, Moderate, Serious, and Extreme CIA impact ratings?"\n    ]\n  },\n  "threat_analysis": [\n    {\n      "threat_id": "threat_1",\n      "threat_name": "Ransomware/Malware Attack and System Compromise",\n      "threat_description": "John Doe''s Laptop, used by Sakthi for critical functions like sales order processing and financial reporting, is highly susceptible to ransomware or advanced malware attacks. The complete absence of an Endpoint Detection and Response (EDR) or Antivirus/Anti-malware solution means that malicious code will likely go undetected and unprevented. Furthermore, security patches and OS updates are applied only annually, leaving the system vulnerable to exploitation of known weaknesses for extended periods. With Sakthi having local administrator privileges, a successful malware infection could easily escalate, encrypting the medium volume of Restricted data, including PHI, PII, PCI_DSS, and Financial_Proprietary information, and potentially spreading to other corporate network resources during occasional remote access.",\n      "impact_assessment": {\n        "confidentiality": {\n          "rating": "Extreme",\n          "numeric_value": 5,\n          "reasoning": "Based on questionnaire answer [3. Data Stored & Processed] stating ''Restricted'' data classification, and sensitive data types [''PHI'', ''PII'', ''PCI_DSS'', ''Financial_Proprietary''] with ''Medium'' volume, unauthorized disclosure due to ransomware would cause ''serious damage to the interests of the organization''. This meets the discovered definition for ''Extreme'' Confidentiality from Asset Management Guidelines_For RM Agentic AI Use Case v1.0.docx (Table 3)."\n        },\n        "integrity": {\n          "rating": "Extreme",\n          "numeric_value": 5,\n          "reasoning": "Based on questionnaire answer [2. Business Context & Usage] listing critical functions like ''Sales order processing'', ''Software development'', ''Financial reporting'', and the presence of ''Restricted'' data including [''PHI'', ''PII'', ''PCI_DSS'', ''Financial_Proprietary''], unauthorized modification or damage by ra