INSERT INTO risks VALUES(28,'RSK-028','Main Headquarters Building','PHYSICAL ASSETS','Prolonged Business Interruption from Environmental Disaster and Power Failure','Risk of prolonged business interruption to the Main Headquarters Building due to environmental disasters (flood, seismic activity, severe weather) and lack of redundant power for critical operations, potentially causing physical damage, extended unavailability of critical functions, and significant operational and financial losses.',4.0,'NON-ACCEPTABLE',4.0,'Low','Low','Low',replace('[\n  {\n    "control_id": "OPS.1.1",\n    "control_name": "Redundant Internet Connectivity",\n    "category": "Preventive",\n    "current_rating": 4,\n    "source": "Questionnaire",\n    "evidence": "User confirmed: ''Yes, for internet only''",\n    "effectiveness": "STRONG"\n  },\n  {\n    "control_id": "A.5.30",\n    "control_name": "Disaster Recovery / Business Continuity Plan (DR/BCP)",\n    "category": "Corrective",\n    "current_rating": 2,\n    "source": "Questionnaire",\n    "evidence": "User confirmed: ''Yes, documented but not tested''",\n    "effectiveness": "LIMITED"\n  }\n]','\n',char(10)),1.66999999999999992,2.33000000000000007,'Unknown',replace('[\n  {\n    "gap_description": "Redundant power sources (UPS, generators) for critical operations are missing",\n    "evidence": "User indicated: ''Yes, for internet only'' regarding redundancy",\n    "impact": "High risk of business interruption due to power failures",\n    "severity": "CRITICAL"\n  },\n  {\n    "gap_description": "Disaster Recovery / Business Continuity Plan is not regularly tested",\n    "evidence": "User indicated: ''Yes, documented but not tested''",\n    "impact": "Uncertainty about the plan''s effectiveness during an actual disaster",\n    "severity": "HIGH"\n  }\n]','\n',char(10)),replace('[\n  {\n    "control_id": "A.7.5",\n    "control_name": "Implement redundant power solutions (UPS and generators) for critical operations",\n    "control_type": "Preventive",\n    "priority": "CRITICAL",\n    "rationale": "Ensures continuous operation of critical systems during power outages.",\n    "expected_effectiveness": 5\n  },\n  {\n    "control_id": "A.5.30",\n    "control_name": "Conduct regular testing and review of the Disaster Recovery and Business Continuity Plan",\n    "control_type": "Corrective",\n    "priority": "HIGH",\n    "rationale": "Validates the plan''s effectiveness and identifies areas for improvement.",\n    "expected_effectiveness": 4\n  }\n]','\n',char(10)),NULL,replace('{\n  "risk_id": "RSK-027",\n  "risk_description": "Prolonged Business Interruption from Environmental Disaster and Power Failure",\n  "root_cause": "Threat: Prolonged Business Interruption from Environmental Disaster and Power Failure. Control Gaps: Redundant power sources for critical operations are missing",\n  "inherent_risk_rating": "4/5",\n  "residual_risk_rating": "2.33/5",\n  "transfer_method": "Insurance",\n  "third_party_name": "ABC Insurance Company Ltd.",\n  "scope_of_transfer": "Business interruption coverage up to $5 million for environmental disasters and power failures. Covers loss of revenue, extra expenses, and recovery costs.",\n  "contract_reference": "INS-2026-BUS-INT-001",\n  "transfer_start_date": "2026-02-03",\n  "transfer_end_date": "2027-02-02",\n  "risk_owner": "Sakthivel",\n  "review_frequency": "Quarterly"\n}','\n',char(10)),'TRANSFER','Sakthivel','Medium','2027-02-02','Open','2026-02-03','2026-02-03',NULL,NULL,NULL,replace('{\n  "discovery_summary": {\n    "cia_methodology": "CIA (Confidentiality, Integrity, Availability)",\n    "cia_rating_scale": "4-level: Insignificant, Moderate, Serious, Extreme",\n    "cia_definitions_source": "Asset Management Guidelines_For RM Agentic AI Use Case v1.0.docx, Table 3",\n    "business_value_calculation_method": "Asset Value Chart/Matrix",\n    "business_value_levels": [\n      "Very Low",\n      "Low",\n      "Medium",\n      "High",\n      "Very High"\n    ],\n    "business_value_source": "Asset Management Guidelines_For RM Agentic AI Use Case v1.0.docx, Table 3 and Risk Assessment Template_For Agentic AI RM Use Case.xlsx, Sheet: Asset Inventorization Guideline",\n    "criticality_classification_method": "Business Value to Criticality Mapping",\n    "criticality_levels": [\n      "Insignificant",\n      "Low Critical",\n      "Medium Critical",\n      "High Critical",\n      "Very High Critical"\n    ],\n    "criticality_source": "Asset Management Guidelines_For RM Agentic AI Use Case v1.0.docx, Table 4 and Risk Assessment Template_For Agentic AI RM Use Case.xlsx, Sheet: Asset Inventorization Guideline",\n    "searches_performed": [\n      "What impact assessment methodology is used? CIA? DREAD? Custom?",\n      "What rating scale is used for Confidentiality, Integrity, and Availability (CIA) impact ratings? What are the levels and definitions for each?",\n      "How is Asset Business Value calculated or determined from CIA impact ratings? Is there a matrix, chart, or formula? What are the Asset Business Value levels?"\n    ]\n  },\n  "threat_analysis": [\n    {\n      "threat_id": "threat_1",\n      "threat_name": "Unauthorized Physical Intrusion and Theft of Critical Assets",\n      "threat_description": "The Main Headquarters Building is vulnerable to unauthorized physical intrusion and theft, particularly outside business hours, due to reliance solely on ''Security Guards (Business Hours)'' for primary access control. The lack of active monitoring for installed CCTV cameras and only ''local alarms'' means that any unauthorized entry into critical areas like the IT data center or executive offices could go undetected for extended periods, allowing malicious actors (e.g., external criminals, disgruntled former employees) to steal sensitive equipment or intellectual property without immediate intervention or alert. This risk is amplified by the building housing critical functions and 1000 personnel, increasing potential targets and insider threat vectors.",\n      "impact_assessment": {\n        "confidentiality": {\n          "rating": "Extreme",\n          "numeric_value": 4,\n          "reasoning": "Based on questionnaire facts stating ''steal sensitive equipment or intellectual property'' from ''IT data center, executive offices'', unauthorized disclosure would cause ''serious damage to the interests of the organization''. This meets the discovered definition for ''Extreme'': ''Unauthorized disclosure within or outside the organization would cause serious damage to the interests of the organization. Disclosure within the organization will be strictly on a \u2018need-to-know\u2019 basis.'' (Asset Management Guidelines_For RM Agentic AI Use Case v1.0.docx, Table 3)."\n        },\n        "integrity": {\n          "rating": "Extreme",\n          "numeric_value": 4,\n          "reasoning": "Based on facts about ''steal sensitive equipment or intellectual property'', theft of critical assets could lead to their unauthorized modification or destruction, or the data on them. This would have a ''severe and long ranging adverse effect on business operations''. This meets the discovered definition for ''Extreme'': ''Unauthorized modification or damage of information is expected to have severe and long ranging adverse effect on business operations.'' (Asset Management Guidelines_For RM Agentic AI Use Case v1.0.docx, Table 3)."\n        },\n        "availability": {\n          "rating": "Extreme",\n          "numeric_value": 4,\n          "reasoning": "Based on facts about ''steal sensitive equipment'' from ''IT data center, executive offices, primary call center'', this would lead to unavailability of assets and services. This would have a ''severe and long ranging adverse effect on business operations''. This meets the discovered definition for ''Extreme'': ''Impact of disruption of access or unavailability of information or information asset or services is expected to have severe and long ranging adverse effect on business operations.'' (Asset Management Guidelines_For RM Agentic AI Use Case v1.0.docx, Table 3)."\n        }\n      },\n      "overall_impact_calculation": {\n        "confidentiality_numeric": 4,\n        "integrity_numeric": 4,\n        "availability_numeric": 4,\n        "calculation_method": "Maximum of C, I, A numeric values",\n        "calculation": "max(4, 4, 4) = 4",\n        "overall_impact": "High",\n        "overall_impact_numeric": 4\n      }\n    },\n    {\n      "threat_id": "threat_2",\n      "threat_name": "Data Center Outage and Building Damage due to Inadequate Fire Suppression",\n      "threat_description": "The IT data center and other critical areas within the Main Headquarters Building are at high risk of significant damage and prolonged outage due to fire. The current fire suppression system relies solely on ''Fire Extinguishers,'' indicating a severe lack of automatic fire detection and suppression systems (e.g., sprinklers, gas suppression for data centers). A fire originating in the data center or other critical areas could rapidly spread, causing extensive damage to IT infrastructure, corporate administrative functions, and executive offices, leading to a complete cessation of IT service delivery, customer support, and product development, impacting 1000 personnel and critical business processes.",\n      "impact_assessment": {\n        "confidentiality": {\n          "rating": "Serious",\n          "numeric_value": 3,\n          "reasoning": "Based on questionnaire facts about ''extensive damage to IT infrastructure, corporate administrative functions, and executive offices'' due to fire, this could lead to destruction of sensitive data or exposure during recovery. This would cause ''significant harm to the interest of the organization''. This meets the discovered definition for ''Serious'': ''Unauthorized disclosure within or outside the organization would cause significant harm to the interest of the organization.'' (Asset Management Guidelines_For RM Agentic AI Use Case v1.0.docx, Table 3)."\n        },\n        "integrity": {\n          "rating": "Extreme",\n          "numeric_value": 4,\n          "reasoning": "Based on facts about ''extensive damage to IT infrastructure'' and ''complete cessation of IT service delivery'', a fire would cause ''unauthorized modification or damage of information'' (destruction) with ''severe and long ranging adverse effect on business operations''. This meets the discovered definition for ''Extreme'': ''Unauthorized modification or damage of information is expected to have severe and long ranging adverse effect on business operations.'' (Asset Management Guidelines_For RM Agentic AI Use Case v1.0.docx, Table 3)."\n        },\n        "availability": {\n          "rating": "Extreme",\n          "numeric_value": 4,\n          "reasoning": "Based on facts about ''prolonged outage'' and ''complete cessation of IT service delivery, customer support, and product development'' impacting ''1000 personnel and critical business processes'', this constitutes a ''severe and long ranging adverse effect on business operations''. This meets the discovered definition for ''Extreme'': ''Impact of disruption of access or unavailability of information or information asset or services is expected to have severe and long ranging adverse effect on business operations.'' (Asset Management Guidelines_For RM Agentic AI Use Case v1.0.docx, Table 3)."\n        }\n      },\n      "overall_impact_calculation": {\n        "confidentiality_numeric": 3,\n        "integrity_numeric": 4,\n        "availability_numeric": 4,\n        "calculation_method": "Maximum of C, I, A numeric values",\n        "calculation": "max(3, 4, 4) = 4",\n        "overall_impact": "High",\n        "overall_impact_numeric": 4\n      }\n    },\n    {\n      "threat_id": "threat_3",\n      "threat_name": "Prolonged Business Interruption from Environmental Disaster and Power Failure",\n      "threat_description": "The Main Headquarters Building faces a high risk of prolonged business interruption from environmental disasters, including floods, seismic activity, and severe weather, given its location in a ''flood zone, seismic activity, severe weather'' prone area. This risk is compounded by the lack of redundant power sources for critical operations (only internet has redundancy), making the IT data center, executive offices, and primary call center highly susceptible to power outages during such events. A severe environmental incident could lead to physical damage, extended power loss, and unavailability of the building, severely impacting daily administrative operations, IT service delivery, customer support, and product development, affecting 1000 personnel.",\n      "impact_assessment": {\n        "confidentiality": {\n          "rating": "Moderate",\n          "numeric_value": 2,\n          "reasoning": "Based on facts about ''physical damage'' and ''unavailability of the building'', physical security could be compromised during recovery, potentially leading to unauthorized access and disclosure of sensitive information. This ''may harm the interest of the organization''. This meets the discovered definition for ''Moderate'': ''Unauthorized disclosure within or outside the organization may harm the interest of the organization.'' (Asset Management Guidelines_For RM Agentic AI Use Case v1.0.docx, Table 3)."\n        },\n        "integrity": {\n          "rating": "Serious",\n          "numeric_value": 3,\n          "reasoning": "Based on facts about ''physical damage, extended power loss'' and ''severely impacting daily administrative operations, IT service delivery'', this could lead to damage or destruction of information and systems, or data corruption. This would have a ''serious adverse effect on business operations''. This meets the discovered definition for ''Serious'': ''Unauthorized modification or damage of information is expected to have a serious adverse effect on business operations.'' (Asset Management Guidelines_For RM Agentic AI Use Case v1.0.docx, Table 3)."\n        },\n        "availability": {\n          "rating": "Extreme",\n          "numeric_value": 4,\n          "reasoning": "Based on facts about ''prolonged business interruption'', ''extended power loss, and unavailability of the building, severely impacting daily administrative operations, IT service delivery, customer support, and product development, affecting 1000 personnel'', this clearly constitutes a ''severe and long ranging adverse effect on business operations''. This meets the discovered definition for ''Extreme'': ''Impact of disruption of access or unavailability of information or information asset or services is expected to have severe and long ranging adverse effect on business operations.'' (Asset Management Guidelines_For RM Agentic AI Use Case v1.0.docx, Table 3)."\n        }\n      },\n      "overall_impact_calculation": {\n        "confidentiality_numeric": 2,\n        "integrity_numeric": 3,\n        "availability_numeric": 4,\n        "calculation_method": "Maximum of C, I, A numeric values",\n        "calculation": "max(2, 3, 4) = 4",\n        "overall_impact": "High",\n        "overall_impact_numeric": 4\n      }\n    },\n    {\n      "threat_id": "threat_4",\n      "threat_name": "Exacerbated Impact from Incidents due to Untested Emergency and Incident Response Plans",\n      "threat_description": "Any significant incident (e.g., fire, environmental disaster, physical security breach) affecting the Main Headquarters Building is likely to have an exacerbated impact and prolonged recovery due to the lack of operational readiness in emergency and incident response. The emergency evacuation plan is ''documented but not regularly practiced,'' the disaster recovery/business continuity plan is ''documented but not tested,'' and the physical security incident response plan is ''documented but not practiced.'' This means that during an actual event involving 1000 personnel and critical functions like the IT data center, there will be confusion, delays in response, and ineffective recovery efforts, leading to higher casualties, greater damage, and extended downtime for daily administrative operations, IT service delivery, and customer support.",\n      "impact_assessment": {\n        "confidentiality": {\n          "rating": "Serious",\n          "numeric_value": 3,\n          "reasoning": "Based on facts about ''exacerbated impact and prolonged recovery'', ''greater damage'', and ''ineffective recovery efforts'', a chaotic recovery could lead to loss of control over sensitive information, causing ''significant harm to the interest of the organization''. This meets the discovered definition for ''Serious'': ''Unauthorized disclosure within or outside the organization would cause significant harm to the interest of the organization.'' (Asset Management Guidelines_For RM Agentic AI Use Case v1.0.docx, Table 3)."\n        },\n        "integrity": {\n          "rating": "Extreme",\n          "numeric_value": 4,\n          "reasoning": "Based on facts about ''greater damage'' and ''ineffective recovery efforts'', information and systems are more likely to be damaged, corrupted, or improperly restored, leading to a ''severe and long ranging adverse effect on business operations''. This meets the discovered definition for ''Extreme'': ''Unauthorized modification or damage of information is expected to have severe and long ranging adverse effect on business operations.'' (Asset Management Guidelines_For RM Agentic AI Use Case v1.0.docx, Table 3)."\n        },\n        "availability": {\n          "rating": "Extreme",\n          "numeric_value": 4,\n          "reasoning": "Based on facts about ''prolonged recovery'' and ''extended downtime for daily administrative operations, IT service delivery, and customer support'', exacerbated by ineffective plans, this clearly indicates a ''severe and long ranging adverse effect on business operations''. This meets the discovered definition for ''Extreme'': ''Impact of disruption of access or unavailability of information or information asset or services is expected to have severe and long ranging adverse effect on business operations.'' (Asset Management Guidelines_For RM Agentic AI Use Case v1.0.docx, Table 3)."\n        }\n      },\n      "overall_impact_calculation": {\n        "confidentiality_numeric": 3,\n        "integrity_numeric": 4,\n        "availability_numeric": 4,\n        "calculation_method": "Maximum of C, I, A numeric values",\n        "calculation": "max(3, 4, 4) = 4",\n        "overall_impact": "High",\n        "overall_impact_numeric": 4\n      }\n    },\n    {\n      "threat_id": "threat_5",\n      "threat_name": "IT Data Center Failure due to Uncontrolled Environmental Conditions",\n      "threat_description": "The IT data center within the Main Headquarters Building is at risk of failure due to uncontrolled environmental conditions. While the building is multi-story and houses a data center, there is no explicit mention of specific environmental controls such as temperature and humidity monitoring systems. This lack of control, especially in a building located in an area prone to ''severe weather,'' can lead to overheating, moisture damage, or other environmental stresses on critical IT equipment. Such conditions could cause hardware malfunctions, data corruption, or complete system shutdowns, directly impacting IT service delivery, customer support, and product development.",\n      "impact_assessment": {\n        "confidentiality": {\n          "rating": "Moderate",\n          "numeric_value": 2,\n          "reasoning": "Based on facts about ''hardware malfunctions, data corruption, or complete system shutdowns'' of the IT data center, catastrophic failure could lead to data loss or unrecoverable sensitive data. This ''may harm the interest of the organization''. This meets the discovered definition for ''Moderate'': ''Unauthorized disclosure within or outside the organization may harm the interest of the organization.'' (Asset Management Guidelines_For RM Agentic AI Use Case v1.0.docx, Table 3)."\n        },\n        "integrity": {\n          "rating": "Extreme",\n          "numeric_value": 4,\n          "reasoning": "Based on facts about ''overheating, moisture damage'' causing ''hardware malfunctions, data corruption, or complete system shutdowns'', this directly represents ''unauthorized modification or damage of information'' (destruction/corruption) with a ''severe and long ranging adverse effect on business operations''. This meets the discovered definition for ''Extreme'': ''Unauthorized modification or damage of information is expected to have severe and long ranging adverse effect on business operations.'' (Asset Management Guidelines_For RM Agentic AI Use Case v1.0.docx, Table 3)."\n        },\n        "availability": {\n          "rating": "Extreme",\n          "numeric_value": 4,\n          "reasoning": "Based on facts about ''IT data center... at risk of failure'' and ''complete system shutdowns, directly impacting IT service delivery, customer support, and product development'', this would have a ''severe and long ranging adverse effect on business operations''. This meets the discovered definition for ''Extreme'': ''Impact of disruption of access or unavailability of information or information asset or services is expected to have severe and long ranging adverse effect on business operations.'' (Asset Management Guidelines_For RM Agentic AI Use Case v1.0.docx, Table 3)."\n        }\n      },\n      "overall_impact_calculation": {\n        "confidentiality_numeric": 2,\n        "integrity_numeric": 4,\n        "availability_numeric": 4,\n        "calculation_method": "Maximum of C, I, A numeric values",\n        "calculation": "max(2, 4, 4) = 4",\n        "overall_impact": "High",\n        "overall_impact_numeric": 4\n      }\n    }\n  ],\n  "asset_cia_ratings": {\n    "confidentiality": {\n      "rating": "Extreme",\n      "numeric_value": 4,\n      "reasoning": "Asset-level CIA: Maximum C value across all threats. Threat 1: Extreme(4), Threat 2: Serious(3), Threat 3: Moderate(2), Threat 4: Serious(3), Threat 5: Moderate(2) \u2192 Asset C = Extreme(4)",\n      "threat_breakdown": [\n        "Threat 1: Extreme(4)",\n        "Threat 2: Serious(3)",\n        "Threat 3: Moderate(2)",\n        "Threat 4: Serious(3)",\n        "Threat 5: Moderate(2)"\n      ]\n    },\n    "integrity": {\n      "rating": "Extreme",\n      "numeric_value": 4,\n      "reasoning": "Asset-level CIA: Maximum I value across all threats. Threat 1: Extreme(4), Threat 2: Extreme(4), Threat 3: Serious(3), Threat 4: Extreme(4), Threat 5: Extreme(4) \u2192 Asset I = Extreme(4)",\n      "threat_breakdown": [\n        "Threat 1: Extreme(4)",\n        "Threat 2: Extreme(4)",\n        "Threat 3: Serious(3)",\n        "Threat 4: Extreme(4)",\n        "Threat 5: Extreme(4)"\n      ]\n    },\n    "availability": {\n      "rating": "Extreme",\n      "numeric_value": 4,\n      "reasoning": "Asset-level CIA: Maximum A value across all threats. Threat 1: Extreme(4), Threat 2: Extreme(4), Threat 3: Extreme(4), Threat 4: Extreme(4), Threat 5: Extreme(4) \u2192 Asset A = Extreme(4)",\n      "threat_breakdown": [\n        "Threat 1: Extreme(4)",\n        "Threat 2: Extreme(4)",\n        "Threat 3: Extreme(4)",\n        "Threat 4: Extreme(4)",\n        "Threat 5: Extreme(4)"\n      ]\n    },\n    "calculation_note": "Asset-level CIA is calculated as MAXIMUM across all threat-level CIA ratings. This represents the worst-case impact scenario for the asset and is used for Asset Business Value and Criticality Classification per organizational Asset Management Guidelines."\n  },\n  "asset_business_value": {\n    "cia_combination": "Extreme, Extreme, Extreme",\n    "cia_combination_key": "ExtremeExtremeExtreme",\n    "business_value_rating": "Very High",\n    "business_value_numeric": 5,\n    "calculation_method": "Asset Value Chart (discovered from Asset Management Guidelines)",\n    "calculation_details": "Per discovered Asset Value Chart (Asset Management Guidelines_For RM Agentic AI Use Case v1.0.docx, Table 3 and Risk Assessment Template_For Agentic AI RM Use Case.xlsx, Sheet: Asset Inventorization Guideline): CIA combination (Integrity: Extreme, Confidentiality: Extreme, Availability: Extreme) maps to Business Value ''Very High''",\n    "source_reference": "Asset Management Guidelines_For RM Agentic AI Use Case v1.0.docx, Table 3 and Risk Assessment Template_For Agentic AI RM Use Case.xlsx, Sheet: Asset Inventorization Guideline",\n    "reasoning": "Based on calculated Asset-level CIA ratings (Confidentiality: Extreme, Integrity: Extreme, Availability: Extreme), per the discovered Asset Value Chart in organizational guidelines, the Asset Business Value is ''Very High''."\n  },\n  "asset_criticality": {\n    "criticality_classification": "Very high Critical",\n    "business_value_input": "Very High",\n    "calculation_method": "Criticality Classification Table (discovered from Asset Management Guidelines)",\n    "calculation_details": "Per discovered Criticality Classification Table (Asset Management Guidelines_For RM Agentic AI Use Case v1.0.docx, Table 4 and Risk Assessment Template_For Agentic AI RM Use Case.xlsx, Sheet: Asset Inventorization Guideline): Business Value ''Very High'' maps to Criticality ''Very high Critical''",\n    "source_reference": "Asset Management Guidelines_For RM Agentic AI Use Case v1.0.docx, Table 4 and Risk Assessment Template_For Agentic AI RM Use Case.xlsx, Sheet: Asset Inventorization Guideline",\n    "reasoning": "Based on calculated Asset Business Value ''Very High'', per the discovered Criticality Classification Table in organizational Asset Management Guidelines, the Asset Criticality Classification is ''Very high Critical''."\n  }\n}','\n',char(10)),replace('{\n  "discovery_summary": {\n    "methodology": "Semi-quantitative Risk Assessment",\n    "risk_calculation": {\n      "method": "Formula",\n      "formula": "Risk Impact Rating \u00d7 Risk Probability Rating",\n      "description": "Risk Value is calculated by multiplying the numeric Risk Impact Rating by the numeric Risk Probability Rating. The result is then mapped to a Risk Evaluation Rating."\n    },\n    "probability_scale": {\n      "type": "Numeric and Categorical",\n      "levels": [\n        {\n          "category": "Very Low",\n          "rating": 1,\n          "definition": "Greater than one year"\n        },\n        {\n          "category": "Low",\n          "rating": 2,\n          "definition": "Once every year"\n        },\n        {\n          "category": "Medium",\n          "rating": 3,\n          "definition": "Moderate / Medium: once every 4-6 months"\n        },\n        {\n          "category": "High",\n          "rating": 4,\n          "definition": "Major / High: once every 1-3 months"\n        },\n        {\n          "category": "Very High",\n          "rating": 5,\n          "definition": "Severe / Very high: more than once per month"\n        }\n      ],\n      "range": "1-5"\n    },\n    "impact_scale": {\n      "type": "Numeric and Categorical",\n      "levels": [\n        {\n          "category": "Very Low",\n          "rating": 1,\n          "definition": "Risk has negligible impact on business and operations"\n        },\n        {\n          "category": "Low",\n          "rating": 2,\n          "definition": "Risk does not have significant impact on business and operations"\n        },\n        {\n          "category": "Medium",\n          "rating": 3,\n          "definition": "Risk has minimum impact on business and operations"\n        },\n        {\n          "category": "High",\n          "rating": 4,\n          "definition": "Risk has serious impact on business and operations"\n        },\n        {\n          "category": "Very High",\n          "rating": 5,\n          "definition": "Risk has catastrophic impact on business and operations"\n        }\n      ],\n      "range": "1-5"\n    },\n    "risk_level_mapping": {\n      "method": "Thresholds based on Risk Value Score",\n      "levels": [\n        {\n          "rating": 1,\n          "score_range": "0 to 5"\n        },\n        {\n          "rating": 2,\n          "score_range": "6 to 10"\n        },\n        {\n          "rating": 3,\n          "score_range": "11 to 15"\n        },\n        {\n          "rating": 4,\n          "score_range": "16 to 20"\n        },\n        {\n          "rating": 5,\n          "score_range": "21 to 25"\n        }\n      ],\n      "thresholds": "Risk Value scores are mapped to a Risk Rating from 1 to 5 based on defined ranges."\n    },\n    "acceptance_criteria": "Risks with a Risk Rating of 1, 2, or 3 are Acceptable. Risks with a Risk Rating of 4 or 5 are Non-acceptable.",\n    "questionnaire_answers_used": true\n  },\n  "summary": {\n    "total_threats_assessed": 5,\n    "highest_risk_value": 16,\n    "non_acceptable_risks_count": 5,\n    "acceptable_risks_count": 0,\n    "overall_risk_level": "NON-ACCEPTABLE"\n  },\n  "threat_risk_quantification": [\n    {\n      "threat": "Prolonged Business Interruption from Environmental Disaster and Power Failure",\n      "vulnerabilities": [\n        "Lack of consideration towards siting of facility in areas susceptible to environmental risks (flood zone, seismic activity).",\n        "Lack of adequate backup measures for backup power supply for critical operations.",\n        "Lack of adequate business continuity and disaster recovery plan (documented but not tested)."\n      ],\n      "risk_statement": "Risk of prolonged business interruption to the Main Headquarters Building due to environmental disasters (flood, seismic activity, severe weather) and lack of redundant power for critical operations, potentially causing physical damage, extended unavailability of critical functions, and significant operational and financial losses.",\n      "risk_impact": {\n        "rating": 4,\n        "category": "High",\n        "reasoning": "Based on Agent 1 CIA assessment: Confidentiality=Moderate (2), Integrity=Serious (3), Availability=Extreme (4). Overall impact is max(2,3,4) = 4 (High)."\n      },\n      "risk_probability": {\n        "rating": 4,\n        "category": "High",\n        "reasoning": "Q17 and Q18 confirm the building is in a ''flood zone, seismic activity, severe weather'' prone area, inherently increasing environmental threat probability. Q16 states redundant power is ''for internet only'', indicating a lack of backup power for critical operations. Q20 indicates the disaster recovery plan is ''documented but not tested'', increasing the likelihood of prolonged interruption."\n      },\n      "risk_value": {\n        "value": 16,\n        "calculation": "4 (impact) \u00d7 4 (probability) = 16"\n      },\n      "risk_evaluation_rating": {\n        "rating": 4,\n        "level": "NON-ACCEPTABLE",\n        "mapping_rationale": "Risk value 16 maps to Risk Rating 4 (16 to 20) per discovered risk evaluation rating table."\n      },\n      "risk_classification": {\n        "classification": "NON-ACCEPTABLE",\n        "criteria_applied": "Risks with a Risk Rating of 4 or 5 are Non-acceptable.",\n        "justification": "Risk Rating 4 is non-acceptable per organizational policy."\n      }\n    }\n  ],\n  "rag_queries_performed": [\n    "risk quantification methodology",\n    "risk calculation formula"\n  ],\n  "rag_sources_consulted": [\n    "Risk Management SOP_For RM Agentic AI Use Case v1.0.docx",\n    "Risk Management Guidelines_For RM Agentic AI Use Case v1.0.docx",\n    "Asset Management Guidelines_For RM Agentic AI Use Case v1.0.docx",\n    "ISO IEC 27005-2022.pdf"\n  ]\n}','\n',char(10)),replace('{\n  "threat_control_evaluation": [\n    {\n      "threat": "Prolonged Business Interruption from Environmental Disaster 