    usage_count INTEGER DEFAULT 0
);
INSERT INTO methodology_cache VALUES(1,'cia_methodology','"CIA (Confidentiality, Integrity, Availability)"','2026-01-28 07:27:57','2026-01-28 07:29:19',2);
INSERT INTO methodology_cache VALUES(2,'cia_rating_scale','"4-level: Insignificant, Moderate, Serious, Extreme"','2026-01-28 07:27:57','2026-01-28 07:29:19',2);
INSERT INTO methodology_cache VALUES(3,'business_value_levels','["Very Low", "Low", "Medium", "High", "Very High"]','2026-01-28 07:27:57','2026-01-28 07:27:57',0);
INSERT INTO methodology_cache VALUES(4,'criticality_levels','["Insignificant", "Low Critical", "Medium Critical", "High Critical", "Very high Critical"]','2026-01-28 07:27:57','2026-01-28 07:27:57',0);
INSERT INTO methodology_cache VALUES(5,'risk_calculation','{"method": "Formula", "formula": "Risk Value (RV) = Risk Impact (RI) \u00d7 Risk Probability (RP)", "description": "Risk Value is computed by multiplying the numerical Risk Impact rating (1-5) by the numerical Risk Probability rating (1-5)."}','2026-01-28 07:27:57','2026-01-28 07:29:19',2);
INSERT INTO methodology_cache VALUES(6,'probability_scale','{"type": "Numeric (1-5) with categorical definitions based on frequency", "levels": ["1: Very Low (Greater than one year)", "2: Low (Once every year)", "3: Medium (Moderate / Medium: once every 4-6 months)", "4: High (Major / High: once every 1-3 months)", "5: Very High (Severe / Very high: more than once per month)"], "range": "1-5"}','2026-01-28 07:27:57','2026-01-28 07:28:56',1);
INSERT INTO methodology_cache VALUES(7,'impact_scale','{"type": "Numeric (1-5) with categorical definitions", "levels": ["1: Very Low (negligible impact)", "2: Low (not significant impact)", "3: Medium (minimum impact)", "4: High (serious impact)", "5: Very High (catastrophic impact)"], "range": "1-5"}','2026-01-28 07:27:57','2026-01-28 07:27:57',0);
INSERT INTO methodology_cache VALUES(8,'risk_level_mapping','{"method": "Risk Value score mapped to a Risk Evaluation Rating (1-5)", "levels": ["1: Very Low", "2: Low", "3: Medium", "4: High", "5: Extreme"], "thresholds": "Risk Value 0-5 -> Rating 1 (Very Low); Risk Value 6-10 -> Rating 2 (Low); Risk Value 11-15 -> Rating 3 (Medium); Risk Value 16-20 -> Rating 4 (High); Risk Value 21-25 -> Rating 5 (Extreme)"}','2026-01-28 07:27:57','2026-01-28 07:27:57',0);
INSERT INTO methodology_cache VALUES(9,'control_framework','"ISO 27001 and IT General Controls (ITGC)"','2026-01-28 07:27:57','2026-01-28 07:28:56',1);
INSERT INTO methodology_cache VALUES(10,'control_effectiveness_scale','{"type": "Numeric", "range": "1-5", "definitions": "1=Non-existent or has major deficiencies; 2=Limited, high level of risk remains, significant deficiencies; 3=Controls in place with few deficiencies in terms of medium risk issues; 4=Designed and operating, with few deficiencies in terms of low risk issues; 5=Designed and operating, with no deficiencies."}','2026-01-28 07:27:57','2026-01-28 07:27:57',0);
INSERT INTO methodology_cache VALUES(11,'control_rating_method','"Weighted average: (Preventive Control Rating \u00d7 1.0 + Detective Control Rating \u00d7 0.75 + Corrective Control Rating \u00d7 0.5) / 3, rounded to two decimal places."','2026-01-28 07:27:57','2026-01-28 07:27:57',0);
INSERT INTO methodology_cache VALUES(12,'residual_risk_method','"Risk Evaluation Rating - Control Rating"','2026-01-28 07:27:57','2026-01-28 07:27:57',0);
