    template_key TEXT NOT NULL,
    template_json TEXT NOT NULL,
    created_date TEXT DEFAULT (datetime('now')),
    last_used TEXT DEFAULT (datetime('now')),
    usage_count INTEGER DEFAULT 0,
    UNIQUE(template_type, template_key)
);
INSERT INTO questionnaire_templates VALUES(1,'DECISION','ACCEPT',replace('{\n  "metadata": {\n    "form_id": "RAF-20260105-120000",\n    "form_name": "Risk Acceptance Form",\n    "generated_date": "2026-01-05",\n    "status": "Completed"\n  },\n  "risk_context": {\n    "risk_id": "RISK-DB-PROD-001-001",\n    "risk_category": "Security Risk",\n    "risk_description": "Security threats to Database Server affecting DB-PROD-001",\n    "current_risk_rating": 4,\n    "calculated_residual_risk": 1.58\n  },\n  "engagement_project": {\n    "engagement_name": "Enterprise Database Security Modernization Program",\n    "project_name": "Phase 1: Production Database Migration to Cloud Infrastructure",\n    "facility_location": "Azure East US 2 Data Center, Virginia, United States",\n    "line_of_business": "Information Technology",\n    "bu_head": "Michael Zhang",\n    "l1_head": "Jennifer Martinez"\n  },\n  "compensating_controls": {\n    "selected_controls": [\n      "Implement Data Encryption at Rest",\n      "Implement Data Encryption in Transit"\n    ]\n  },\n  "justification": {\n    "justification_text": "Primary reason for accepting this risk: Cost of treatment is prohibitive",\n    "valid_until_date": "2026-01-31"\n  },\n  "approvals": {\n    "risk_approved_by_role": "Risk & Compliance Apex Committee",\n    "approver": {\n      "name": "Lisa Anderson",\n      "designation": "CISO",\n      "employee_id": "EMP56789"\n    },\n    "risk_owned_by_role": "CIO/Legal/L1 Head",\n    "owner": {\n      "name": "John Doe",\n      "designation": "Senior IT Manager - Database Operations",\n      "employee_id": "EMP34567"\n    },\n    "client_ciso_name": "",\n    "client_approval_evidence": ""\n  },\n  "signoff": {\n    "signoff_name": "",\n    "signoff_signature": "",\n    "signoff_date": "2026-01-05"\n  }\n}','\n',char(10)),'2026-01-28 07:27:57','2026-01-28 07:28:56',1);
INSERT INTO questionnaire_templates VALUES(2,'DECISION','TRANSFER',replace('{\n  "risk_context": {\n    "Risk ID": "RSK-011",\n    "Risk Description": "Physical Theft and Sensitive Data Exposure of John Doe''s Laptop",\n    "Inherent Risk Rating": "4/5/5",\n    "Residual Risk Rating": "3.0/5",\n    "generation_date": "2026-01-16"\n  },\n  "sections": [\n    {\n      "title": "Risk Transfer Details",\n      "fields": [\n        {\n          "field_name": "Risk ID",\n          "value": "RSK-011"\n        },\n        {\n          "field_name": "Risk Description",\n          "value": "Physical Theft and Sensitive Data Exposure of John Doe''s Laptop"\n        },\n        {\n          "field_name": "Root Cause",\n          "value": "Threat: Physical Theft and Sensitive Data Exposure. Control Gaps: Remote Wipe or Lock capability unknown/missing"\n        },\n        {\n          "field_name": "Inherent Risk Rating",\n          "value": "4/5/5"\n        },\n        {\n          "field_name": "Transfer Method",\n          "value": "Insurance"\n        },\n        {\n          "field_name": "Third Party Name",\n          "value": "CyberGuard Insurance Ltd"\n        },\n        {\n          "field_name": "Scope of Transfer",\n          "value": "Data loss, corruption, and recovery costs up to $50,000 per incident. Covers hardware replacement, data recovery services, forensic investigation, and business interruption losses due to laptop failure or malware incidents."\n        },\n        {\n          "field_name": "Contract Reference",\n          "value": "CYB-2025-LAP-00789"\n        },\n        {\n          "field_name": "Transfer Start Date",\n          "value": "2026-01-16"\n        },\n        {\n          "field_name": "Transfer End Date",\n          "value": "2026-01-31"\n        },\n        {\n          "field_name": "Residual Risk Rating",\n          "value": "3.0/5"\n        },\n        {\n          "field_name": "Risk Owner",\n          "value": "John Smith (IT Security Manager)"\n        },\n        {\n          "field_name": "Review Frequency",\n          "value": "Quarterly"\n        },\n        {\n          "field_name": "Next Review Date",\n          "value": "2026-01-31"\n        }\n      ]\n    }\n  ],\n  "generation_date": "2026-01-16 15:32:46"\n}','\n',char(10)),'2026-01-28 07:27:57','2026-02-02 09:59:22',2);
INSERT INTO questionnaire_templates VALUES(3,'DECISION','TERMINATE',replace('{\012  "risk_context": {\012    "risk_id": "RSK-012",\012    "risk_description": "Data Loss or Corruption from System Failure or Undetected Malware",\012    "inherent_risk_rating": "5/5/5",\012    "residual_risk_rating": "5.0/5/5",\012    "root_cause": "Threat: Data Loss or Corruption from System Failure or Undetected Malware. Control Gaps: Endpoint Detection and Response (EDR) or Antivirus/Anti-malware solution missing",\012    "generation_date": "2026-01-16"\012  },\012  "sections": [\012    {\012      "title": "Risk Information (AI Pre-filled)",\012      "fields": [\012        {\012          "field_name": "Risk ID",\012          "value": "RSK-012"\012        },\012        {\012          "field_name": "Risk Description",\012          "value": "Data Loss or Corruption from System Failure or Undetected Malware"\012        },\012        {\012          "field_name": "Root Cause",\012          "value": "Threat: Data Loss or Corruption from System Failure or Undetected Malware. Control Gaps: Endpoint Detection and Response (EDR) or Antivirus/Anti-malware solution missing"\012        },\012        {\012          "field_name": "Inherent Risk Rating",\012          "value": "5/5/5"\012        },\012        {\012          "field_name": "Residual Risk",\012          "value": "5.0/5/5"\012        }\012      ]\012    },\012    {\012      "title": "Risk Termination Details",\012      "fields": [\012        {\012          "field_name": "Termination Justification",\012          "value": "The organization has decided to decommission John Doe''s laptop as part of a company-wide hardware refresh program. All data will be migrated to a new corporate-managed device with enhanced security controls including EDR, full disk encryption, and automated backup. This risk will be eliminated by removing the asset from the environment entirely."\012        },\012        {\012          "field_name": "Business Impact",\012          "value": "Positive impact: Elimination of high-risk legacy device reduces overall organizational risk exposure. User productivity will be maintained through seamless migration to new hardware. No business disruption expected as migration is planned during scheduled maintenance window. Cost savings from reduced security incidents and improved compliance posture."\012        },\012        {\012          "field_name": "Approval Authority",\012          "value": "IT Security Committee and Chief Information Security Officer (CISO)"\012        },\012        {\012          "field_name": "Termination Actions",\012          "value": "1. Backup all critical data from John Doe''s laptop to secure cloud storage\n2. Provision new corporate laptop with Windows 11, EDR solution, and full disk encryption\n3. Migrate user profile and applications to new device\n4. Securely wipe old laptop using DoD 5220.22-M standard\n5. Physically destroy hard drive and dispose through certified e-waste vendor\n6. Update asset inventory to reflect device retirement\n7. Verify user access and functionality on new device\n8. Close risk ticket in risk register"\012        },\012        {\012          "field_name": "Completion Date",\012          "value": "2026-01-16"\012        },\012        {\012          "field_name": "Closure Status",\012          "value": "Closed"\012        }\012      ]\012    }\012  ],\012  "generation_date": "2026-01-16 16:43:03"\012}','\012',char(10)),'2026-01-28 07:27:57','2026-01-28 07:28:56',1);
INSERT INTO questionnaire_templates VALUES(4,'FOLLOWUP','ACCEPT','[{"followup_date": "2026-01-18 20:26:44", "decision_type": "ACCEPT", "questionnaire_type": "ACCEPT Follow-up", "answers": {"action_owner": "John Smith - Database Administrator", "reason_for_accepting_risk": "Cost of implementing full encryption exceeds business value. Current access controls and monitoring provide adequate protection for non-sensitive data.\n", "management_approval_valid_till": "Approved by CISO Sarah Johnson, valid until 2026-04-05", "risk_within_appetite": "Yes", "impact_increased_since_acceptance": "No", "compensating_controls_in_place": "Yes - Enhanced monitoring with SIEM alerts, quarterly access reviews, database activity monitoring enabled, backup encryption implemented\n", "evidence_of_acceptance": "No (please specify)", "progress": "Compensating controls are 75% implemented. SIEM alerts configured and operational. Access reviews completed for Q1. Database activity monitoring deployed. Backup encryption pending final testing. No security incidents reported. Risk remains stable and within appetite.", "expected_completion_date": "2026-02-15", "reason_for_delay": "Backup encryption testing delayed by 2 weeks due to vendor support availability. No impact on risk level.\n", "final_confirmation_sign_off": "John Smith - Database Administrator, 2026-01-18", "comments_additional_notes": "All compensating controls performing as expected. Quarterly review scheduled for April 2026. Recommend continuing acceptance with current controls.", "asset_product_owner_confirmation": "Confirmed by Mike Chen - Database Product Owner, 2026-01-18"}, "summary": {}}]','2026-01-28 07:27:57','2026-01-28 07:28:56',1);
