INSERT INTO pending_questionnaires VALUES('61567da5-bf59-4ccc-9b91-09f260c737fc','John Doe''s Laptop','FOLLOWUP_TERMINATE','{"questionnaire_title": "Risk Follow-up Questionnaire", "questionnaire_metadata": {"questionnaire_type": "TERMINATE Follow-up", "generation_date": "2026-02-03", "days_since_identification": 18, "target_completion_date": "2026-01-16", "days_until_target": "-19"}, "risk_context": {"risk_id": "RSK-012", "asset": "John Doe''s Laptop", "threat": "Data Loss or Corruption from System Failure or Undetected Malware", "treatment_decision": "TERMINATE"}, "sections": [{"title": "Section 1: Risk Context", "description": "Pre-filled from database", "fields": [{"id": "risk_id", "field_name": "Risk ID", "type": "display", "value": "RSK-012"}, {"id": "asset_name", "field_name": "Asset Name", "type": "display", "value": "John Doe''s Laptop"}, {"id": "threat_name", "field_name": "Risk Description", "type": "display", "value": "Data Loss or Corruption from System Failure or Undetected Malware"}, {"id": "treatment_decision", "field_name": "Management Decision", "type": "display", "value": "TERMINATE"}, {"id": "termination_justification", "field_name": "Termination Justification", "type": "display", "value": "The organization has decided to decommission John Doe''s laptop as part of a company-wide hardware refresh program. All data will be migrated to a new corporate-managed device with enhanced security controls including EDR, full disk encryption, and automated backup. This risk will be eliminated by removing the asset from the environment entirely."}, {"id": "business_impact", "field_name": "Business Impact", "type": "display", "value": "Positive impact: Elimination of high-risk legacy device reduces overall organizational risk exposure. User productivity will be maintained through seamless migration to new hardware. No business disruption expected as migration is planned during scheduled maintenance window. Cost savings from reduced security incidents and improved compliance posture."}, {"id": "termination_actions", "field_name": "Termination Actions", "type": "display", "value": "1. Backup all critical data from John Doe''s laptop to secure cloud storage\n2. Provision new corporate laptop with Windows 11, EDR solution, and full disk encryption\n3. Migrate user profile and applications to new device\n4. Securely wipe old laptop using DoD 5220.22-M standard\n5. Physically destroy hard drive and dispose through certified e-waste vendor\n6. Update asset inventory to reflect device retirement\n7. Verify user access and functionality on new device\n8. Close risk ticket in risk register"}, {"id": "action_owner", "field_name": "Action Owner (Person responsible for implementation)", "type": "text", "placeholder": "Enter name of person responsible"}]}, {"title": "Section 2: TERMINATE Questionnaire", "description": "Decision-specific follow-up questions", "fields": [{"id": "termination_approach", "field_name": "Termination approach", "type": "select", "options": ["Asset decommissioned", "Process stopped", "Application retired", "Activity discontinued"]}, {"id": "termination_date", "field_name": "Date of Termination / Decommissioning", "type": "date"}, {"id": "dependencies_addressed", "field_name": "Confirmation that all dependencies are addressed", "type": "select", "options": ["Yes", "Partially", "No"]}, {"id": "residual_risk_level", "field_name": "Current Residual Risk Level", "type": "select", "options": ["Low", "Medium", "High"]}, {"id": "termination_evidence", "field_name": "Evidence of termination available", "type": "select", "options": ["Yes [attach reference]", "No"]}]}, {"title": "Section 3: Progress", "description": "Overall progress tracking", "fields": [{"id": "overall_completion", "field_name": "Completion Percentage (if in progress)", "type": "number", "placeholder": "___ %"}, {"id": "expected_completion", "field_name": "Expected Completion Date", "type": "date"}, {"id": "delay_reason", "field_name": "Reason for Delay (if applicable)", "type": "textarea"}]}, {"title": "Section 4: Final Sign-off", "description": "Confirmation and approval", "fields": [{"id": "comments", "field_name": "Comments / additional notes (If any)", "type": "textarea"}, {"id": "owner_confirmation", "field_name": "Asset / Product owner confirmation", "type": "text", "placeholder": "Name  Signature  Date"}]}]}',NULL,'sakthivel.r@yash.com','Pending','2026-02-03 13:23:12',NULL,NULL,'RSK-012');
INSERT INTO pending_questionnaires VALUES('0ac07935-cad6-4ba2-ada0-843af7e2c90d','SAP ERP','Agent0','{"questionnaire_title": "Risk Assessment Questionnaire for SAP ERP", "asset_type_analyzed": "SAP ERP", "intelligence_summary": {"asset_understanding": "SAP ERP is a mission-critical Software Asset (Application Software) that integrates core business processes such as finance, HR, manufacturing, and supply chain. It typically stores highly sensitive business and personal data and has numerous internal and external dependencies.", "key_risk_factors": ["Data classification (PII, Financial, Intellectual Property)", "Number of internal and external users", "Administrative access levels", "Network exposure (Internet vs. Internal)", "Integration with other critical systems", "Status of security controls like MFA, encryption, and logging", "Backup and Disaster Recovery readiness", "Patching and vulnerability management frequency"], "methodology_discovered": "The organization uses a 1-5 numerical scale for both Impact and Probability. Risk Value is calculated as a product of these, and risks are evaluated based on a 1-5 rating system. Controls are categorized as Preventive, Detective, and Corrective, and their effectiveness influences the Residual Risk calculation.", "scales_discovered": "Impact Scale: 1 (Very Low) to 5 (Very High). Probability Scale: 1 (Very Low - >1 year) to 5 (Very High - >1 per month).", "why_these_questions": "These questions focus on collecting objective facts about the SAP environment and the owner''s high-level perspective on impact and likelihood. This allows technical analysts to determine CIA ratings and control effectiveness without requiring the asset owner to perform complex risk modeling.", "searches_performed": ["asset inventory structure asset identification fields asset type categories", "risk assessment methodology impact probability rating scales control framework"]}, "sections": [{"section_name": "Section 1: Asset Identification", "section_purpose": "To uniquely identify the asset and its ownership within the organization''s inventory.", "questions": [{"question_id": "Q_ASSET_CLASS_NAME", "question_text": "What is the specific name/title of this asset?", "question_type": "text", "required": true, "help_text": "e.g., SAP S/4HANA Production, SAP ERP Finance Module", "why_this_matters": "Required for unique identification in the asset inventory."}, {"question_id": "Q_ASSET_TYPE", "question_text": "What type of asset is this?", "question_type": "dropdown", "options": [{"value": "Physical Assets", "label": "Physical Assets"}, {"value": "Software Assets", "label": "Software Assets"}, {"value": "Information Assets", "label": "Information Assets"}, {"value": "Service Assets", "label": "Service Assets"}, {"value": "People Assets", "label": "People Assets"}], "required": true, "help_text": "Select the primary category for this asset.", "why_this_matters": "Determines the applicable control set and assessment logic."}, {"question_id": "Q_ASSET_DESCRIPTION", "question_text": "Provide a brief description of the asset''s function.", "question_type": "textarea", "required": true, "help_text": "Describe what business processes this SAP instance supports.", "why_this_matters": "Provides context for the risk analyst to understand business impact."}, {"question_id": "Q_ASSET_CLASS_OWNER", "question_text": "Who is the designated Asset Class Owner?", "question_type": "text", "required": true, "help_text": "The person responsible for the maintenance and implementation of controls.", "why_this_matters": "Identifies the individual accountable for the asset''s risk management."}, {"question_id": "Q_LOCATION", "question_text": "Where is this asset logically or physically located?", "question_type": "text", "required": true, "help_text": "e.g., On-premise Data Center (Pune), AWS Cloud (Region), Azure.", "why_this_matters": "Location influences the threat landscape and physical/environmental controls."}]}, {"section_name": "Section 2: Business Context & Data Facts", "section_purpose": "To understand the criticality of the data and the scale of the asset''s usage.", "questions": [{"question_id": "Q_DATA_TYPES", "question_text": "What types of sensitive data does this SAP ERP store or process?", "question_type": "multiselect", "options": [{"value": "PII", "label": "Personally Identifiable Information (PII)"}, {"value": "Financial", "label": "Financial/Accounting Records"}, {"value": "Intellectual Property", "label": "Intellectual Property / Trade Secrets"}, {"value": "HR", "label": "Human Resources / Payroll Data"}, {"value": "Customer", "label": "Customer/Vendor Master Data"}, {"value": "None", "label": "None of the above"}], "required": true, "help_text": "Select all that apply.", "why_this_matters": "Directly influences the Confidentiality and Integrity impact analysis."}, {"question_id": "Q_USER_COUNT", "question_text": "Approximately how many users access this system?", "question_type": "dropdown", "options": [{"value": "1-50", "label": "1-50"}, {"value": "51-500", "label": "51-500"}, {"value": "501-5000", "label": "501-5000"}, {"value": "5000+", "label": "More than 5000"}], "required": true, "help_text": "Include both internal employees and external partners.", "why_this_matters": "Higher user counts increase the attack surface and potential impact of downtime."}, {"question_id": "Q_INTERNET_EXPOSURE", "question_text": "Is any part of this SAP ERP accessible directly from the internet?", "question_type": "dropdown", "options": [{"value": "Yes", "label": "Yes (e.g., Fiori apps, Web GUI)"}, {"value": "No", "label": "No (Internal Network/VPN only)"}, {"value": "Partial", "label": "Partially (via specific API gateways/proxies)"}], "required": true, "help_text": "Indicate if the system has a public-facing component.", "why_this_matters": "Internet exposure significantly increases the probability of external threats."}]}, {"section_name": "Section 3: Security Control Facts", "section_purpose": "To collect factual information about the technical and procedural controls currently in place.", "questions": [{"question_id": "Q_MFA_ENABLED", "question_text": "Is Multi-Factor Authentication (MFA) required for all users?", "question_type": "dropdown", "options": [{"value": "Yes", "label": "Yes, for all users"}, {"value": "Admins Only", "label": "Only for Administrative/Privileged users"}, {"value": "No", "label": "No MFA in place"}], "required": true, "help_text": "MFA includes tokens, biometrics, or app-based codes.", "why_this_matters": "MFA is a critical preventive control against unauthorized access."}, {"question_id": "Q_ENCRYPTION_AT_REST", "question_text": "Is the SAP database encrypted at rest?", "question_type": "dropdown", "options": [{"value": "Yes", "label": "Yes, fully encrypted"}, {"value": "Partial", "label": "Partially encrypted"}, {"value": "No", "label": "No"}], "required": true, "help_text": "Refers to Transparent Data Encryption (TDE) or similar technologies.", "why_this_matters": "Protects data confidentiality in case of physical or storage-level theft."}, {"question_id": "Q_BACKUP_FREQUENCY", "question_text": "How frequently are full system backups performed?", "question_type": "dropdown", "options": [{"value": "Daily", "label": "Daily or more frequent"}, {"value": "Weekly", "label": "Weekly"}, {"value": "Monthly", "label": "Monthly"}, {"value": "None", "label": "No regular backups"}], "required": true, "help_text": "Indicate the frequency of full data backups.", "why_this_matters": "Determines the potential data loss (RPO) and availability recovery capability."}, {"question_id": "Q_PATCHING_CYCLE", "question_text": "How often are SAP security patches (Security Notes) applied?", "question_type": "dropdown", "options": [{"value": "Monthly", "label": "Monthly (Standard SAP Patch Day)"}, {"value": "Quarterly", "label": "Quarterly"}, {"value": "Ad-hoc", "label": "Ad-hoc / When critical"}, {"value": "Rarely", "label": "Rarely or Never"}], "required": true, "help_text": "Refers to the regular application of SAP security updates.", "why_this_matters": "Factual indicator of vulnerability management effectiveness."}, {"question_id": "Q_LOGGING_MONITORING", "question_text": "Are SAP security audit logs actively monitored by a SOC or security team?", "question_type": "dropdown", "options": [{"value": "Yes", "label": "Yes, 24/7 monitoring"}, {"value": "Review Only", "label": "Logs are kept but only reviewed after incidents"}, {"value": "No", "label": "No logging/monitoring"}], "required": true, "help_text": "Indicate if there is active detection for suspicious activity.", "why_this_matters": "Determines the organization''s detective control capability."}]}, {"section_name": "Section 4: Overall Risk Perspective", "section_purpose": "To capture the asset owner''s expert opinion on the overall impact and likelihood of a compromise.", "questions": [{"question_id": "Q_OVERALL_IMPACT", "question_text": "Considering all business dependencies, what is the estimated overall ''Impact Rating'' if this SAP ERP were to be severely compromised (e.g., total data loss or 1-week outage)?", "question_type": "dropdown", "options": [{"value": "1", "label": "1 - Very Low (Negligible impact)"}, {"value": "2", "label": "2 - Low (No significant impact)"}, {"value": "3", "label": "3 - Medium (Minimum impact)"}, {"value": "4", "label": "4 - High (Serious impact)"}, {"value": "5", "label": "5 - Very High (Catastrophic impact)"}], "required": true, "help_text": "Based on the organization''s 1-5 impact scale.", "why_this_matters": "Captures the owner''s high-level view of business consequence."}, {"question_id": "Q_OVERALL_PROBABILITY", "question_text": "Considering current threats and your knowledge of the system, what is the estimated ''Probability Rating'' of a severe compromise occurring within the next year?", "question_type": "dropdown", "options": [{"value": "1", "label": "1 - Very Low (Greater than one year)"}, {"value": "2", "label": "2 - Low (Once every year)"}, {"value": "3", "label": "3 - Medium (Once every 4-6 months)"}, {"value": "4", "label": "4 - High (Once every 1-3 months)"}, {"value": "5", "label": "5 - Very High (More than once per month)"}], "required": true, "help_text": "Based on the organization''s 1-5 probability scale.", "why_this_matters": "Captures the owner''s perspective on the likelihood of threat exploitation."}]}]}',NULL,'vaishnavi.tudhigani@yash.com','Pending','2026-02-03 19:03:01',NULL,NULL,NULL);
