INSERT INTO sessions VALUES(16,'session_John_Does_Laptop_20260113_200337','{"session_id": "session_John_Does_Laptop_20260113_200337", "asset_data": {"asset_name": "John Doe''s Laptop", "asset_type": "Physical Assets", "asset_owner": " IT Department", "location": "Head Office - Floor 3", "description": "4", "questionnaire_answers": {"Q_ASSET_CLASS_NAME": {"question_text": "What is the specific name or identifier for this Employee Laptop (e.g., ''John Doe''s Laptop'', ''HR-Laptop-001'')?", "answer": "John Doe''s Laptop", "question_type": "text", "section": "1. Asset Identification", "help_text": "Provide a unique and descriptive name or ID for this specific laptop.", "why_this_matters": "Ensures the asset is uniquely identifiable in the inventory for tracking and management."}, "Q_TYPE_OF_ASSET": {"question_text": "What type of asset is this?", "answer": "Physical Assets", "question_type": "dropdown", "section": "1. Asset Identification", "help_text": "Select the primary category that best describes this asset. For an Employee Laptop, ''Physical Assets'' is typically appropriate.", "why_this_matters": "Categorizes the asset according to organizational standards, guiding subsequent risk analysis."}, "Q_ASSET_CLASS_OWNER": {"question_text": "Who is the primary Asset Class Owner (e.g., IT Department, specific business unit head) responsible for this laptop''s overall management and security?", "answer": " IT Department", "question_type": "text", "section": "1. Asset Identification", "help_text": "Specify the individual or department accountable for the asset.", "why_this_matters": "Identifies accountability for the asset and its associated risks."}, "Q_CUSTODIAN": {"question_text": "Who is the primary user/custodian of this Employee Laptop?", "answer": "Sakthi", "question_type": "text", "section": "1. Asset Identification", "help_text": "Provide the name or employee ID of the individual primarily using this laptop.", "why_this_matters": "Identifies the direct user, which is critical for understanding usage patterns and direct responsibility."}, "Q_LOCATION": {"question_text": "What is the primary physical location where this laptop is used (e.g., ''Head Office - Floor 3'', ''Remote - USA'', ''Field Operations'')?", "answer": "Head Office - Floor 3", "question_type": "text", "section": "1. Asset Identification", "help_text": "Indicate the typical physical environment of use.", "why_this_matters": "Location can influence physical security controls and environmental risks."}, "Q_UNIQUE_IDENTIFIER": {"question_text": "Does this laptop have a unique organizational identifier (e.g., asset tag, serial number, inventory ID)? If yes, please provide it.", "answer": "Dell-x8368", "question_type": "text", "section": "1. Asset Identification", "help_text": "Enter the unique ID if available.", "why_this_matters": "Provides an additional unique identifier for asset tracking and management."}, "Q_BUSINESS_CRITICALITY": {"question_text": "What is the overall business criticality of this Employee Laptop to the organization''s operations?", "answer": "4", "question_type": "dropdown", "section": "2. Business Context & Usage", "help_text": "Consider the impact on business processes if this laptop were unavailable or compromised. This is an overall business value, not a security impact rating.", "why_this_matters": "Helps prioritize the asset for risk assessment and resource allocation."}, "Q_BUSINESS_PROCESSES_DEPENDENT": {"question_text": "List the primary business processes or functions that heavily rely on this Employee Laptop.", "answer": "''Sales order processing'', ''Software development'', ''Financial reporting'', ''Customer support''", "question_type": "textarea", "section": "2. Business Context & Usage", "help_text": "e.g., ''Sales order processing'', ''Software development'', ''Financial reporting'', ''Customer support''.", "why_this_matters": "Identifies the direct operational impact if the asset is compromised or unavailable."}, "Q_REMOTE_ACCESS_FREQUENCY": {"question_text": "How frequently is this laptop used for remote access to corporate resources (e.g., VPN, cloud services)?", "answer": "Occasionally (1-3 times a month)", "question_type": "dropdown", "section": "2. Business Context & Usage", "help_text": "Indicate how often the laptop connects to internal systems from outside the corporate network.", "why_this_matters": "Remote access increases exposure to external threats and requires specific controls."}, "Q_DATA_CLASSIFICATION": {"question_text": "What is the highest data classification level of information typically stored or processed on this Employee Laptop?", "answer": "Restricted", "question_type": "dropdown", "section": "3. Data Stored & Processed", "help_text": "Select the highest classification based on organizational data classification policies.", "why_this_matters": "Directly indicates the potential impact of a data breach on confidentiality."}, "Q_SENSITIVE_DATA_TYPES": {"question_text": "Does this Employee Laptop store or process any of the following sensitive data types?", "answer": ["PHI", "PII", "PCI_DSS", "Financial_Proprietary"], "question_type": "multiselect", "section": "3. Data Stored & Processed", "help_text": "Select all applicable types of sensitive data.", "why_this_matters": "Specific data types trigger regulatory compliance requirements and higher impact levels."}, "Q_DATA_VOLUME": {"question_text": "Estimate the volume of sensitive data (if any) stored on this laptop.", "answer": "Medium (e.g., hundreds of documents/records)", "question_type": "dropdown", "section": "3. Data Stored & Processed", "help_text": "Provide an approximate volume of sensitive data.", "why_this_matters": "The volume of sensitive data directly correlates with the scale of potential impact in case of a breach."}, "Q_DISK_ENCRYPTION_ENABLED": {"question_text": "Is full disk encryption enabled and enforced on this Employee Laptop (e.g., BitLocker, FileVault)?", "answer": "Yes", "question_type": "dropdown", "section": "4. Technical & Security Configuration Facts", "help_text": "Confirms if data at rest is protected against unauthorized access if the device is lost or stolen.", "why_this_matters": "Full disk encryption is a primary control against data loss from physical theft or loss."}, "Q_MFA_FOR_LOGIN": {"question_text": "Is Multi-Factor Authentication (MFA) required for logging into this Employee Laptop or accessing its primary operating system?", "answer": "No", "question_type": "dropdown", "section": "4. Technical & Security Configuration Facts", "help_text": "Indicate if an additional authentication factor (e.g., token, biometric) is needed beyond a password.", "why_this_matters": "MFA significantly reduces the risk of unauthorized access due to compromised credentials."}, "Q_ENDPOINT_PROTECTION_INSTALLED": {"question_text": "Is an Endpoint Detection and Response (EDR) or Antivirus/Anti-malware solution installed and actively running on this laptop?", "answer": "No", "question_type": "dropdown", "section": "4. Technical & Security Configuration Facts", "help_text": "Confirms the presence of security software to detect and prevent malicious activity.", "why_this_matters": "Endpoint protection is critical for defending against malware, viruses, and other cyber threats."}, "Q_PATCHING_FREQUENCY": {"question_text": "How frequently are security patches and operating system updates applied to this Employee Laptop?", "answer": "Annually", "question_type": "dropdown", "section": "4. Technical & Security Configuration Facts", "help_text": "Indicate the typical schedule for applying security updates.", "why_this_matters": "Regular patching addresses known vulnerabilities, reducing the attack surface."}, "Q_BACKUP_SOLUTION": {"question_text": "Is there a centralized or automated backup solution in place for critical data stored on this Employee Laptop?", "answer": "Yes, automated to cloud/network storage", "question_type": "dropdown", "section": "4. Technical & Security Configuration Facts", "help_text": "Confirms if data is regularly backed up to prevent loss.", "why_this_matters": "Backups are essential for data recovery in case of system failure, corruption, or ransomware attacks."}, "Q_REMOTE_WIPE_CAPABILITY": {"question_text": "Does the organization have the capability to remotely wipe or lock this Employee Laptop if it is lost or stolen?", "answer": "Unknown", "question_type": "dropdown", "section": "4. Technical & Security Configuration Facts", "help_text": "Indicate if remote data destruction or device locking is possible.", "why_this_matters": "Remote wipe/lock is a critical control to prevent data exfiltration from lost or stolen devices."}, "Q_ADMIN_PRIVILEGES": {"question_text": "Does the primary user of this laptop have local administrator privileges?", "answer": "Yes", "question_type": "dropdown", "section": "4. Technical & Security Configuration Facts", "help_text": "Indicate if the user can install software or make system-wide changes.", "why_this_matters": "Restricting admin privileges reduces the risk of malware installation and unauthorized system changes."}, "Q_PHYSICAL_SECURITY_POLICY": {"question_text": "Are there organizational policies or guidelines in place regarding the physical security of employee laptops (e.g., ''do not leave unattended'', ''secure in office'')?", "answer": "Yes, but not strictly enforced", "question_type": "dropdown", "section": "5. Physical & Environmental Control Facts", "help_text": "Indicate if policies exist and their level of enforcement.", "why_this_matters": "Policies guide user behavior to protect the physical asset."}, "Q_TRAVEL_FREQUENCY": {"question_text": "How frequently does this Employee Laptop travel outside of secure corporate premises (e.g., for business travel, remote work from public places)?", "answer": "Occasionally (1-3 times a month)", "question_type": "dropdown", "section": "5. Physical & Environmental Control Facts", "help_text": "Estimate how often t